mapper.select:
select list_no, id, subject, content, register_datetime
from (
        select rownum rn, list_no, id, subject, content, register_datetime 
        from (
                select  list_no, id, subject, content, register_datetime
                from STAYPC_NOTICE
                order by STAYPC_NOTICE.LIST_NO desc))
where rn between #{begin} and #{end}
order by rn asc
